package com.tns.basics;

public class LogicalOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     boolean b1=true, b2=false;
     
     //Logical AND
     System.out.println("b1 && b2 :"+ (b1 && b2));
     //Logical OR
     System.out.println("b1 || b2 :"+ (b1 || b2));
     //Logical NOT
     System.out.println("!(b1 && b2) :"+ !(b1 && b2));
     
	}

}
