package com.tns.basics;

public class RelationalOp {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int n1=12, n2=2;
        //is equal to
        System.out.println("n1==n2 : "+(n1==n2));
        //is not equal to
        System.out.println("n1!=n2 : "+(n1!=n2));
        //Greater than
        System.out.println("n1>n2 : "+(n1>n2));
        //Less than
        System.out.println("n1<n2 : "+(n1<n2));
        //Greater than equal to
        System.out.println("n1>=n2 : "+(n1>=n2));
        // less than equal to
        System.out.println("n1<=n2 : "+(n1<=n2));




	}

}
